INSERT INTO `sach` (`Masach`, `TenSach`, `SoLuongTon`, `NgayCapNhat`, `AnhBia`, `MoTa`, `GiaBan`, `tacgia`) VALUES
(2, 'Kỹ Năng Thuyết Phục - Hạ Gục Đối Phương', 20, '2023-10-15', '../../img/img_sach/h2.jpg\r\n', 'Giới thiệu sách Kỹ Năng Thuyết Phục - Hạ Gục Đối Phương\r\nSảy chân, gượng lại còn vừa,\r\n\r\nSảy miệng, biết nói làm sao bây giờ.\r\n\r\nLời nói ra không thể lấy lại, thế nên ông bà mới có câu “Uốn lưỡi bảy lần trước khi nói.”\r\n\r\nTrong khi nhiều người có thể nghĩ rằng lập luận, thuyết trình logic là tài năng bẩm sinh, thực chất kỹ năng này có thể được rèn giũa nếu bạn biết cách trau dồi.\r\n\r\nCó thể bạn chưa biết thành phần cấu tạo nên “sự thuyết phục”. Đó là sự rung động bằng tình cảm và sự thấu hiểu bằng lý trí, vận dụng các phương thức biểu đạt logic và kỹ thuật trong giao tiếp để thuyết phục người đối diện.\r\n\r\n“Nếu tình cảm đứng về phía bạn, hãy dùng tình cảm; nếu đạo lý đứng về phía bạn, hãy dùng đạo lý; nếu pháp luật đứng về phía bạn, hãy dùng pháp luật để nói chuyện với đối phương.”', '104.000', 'Trần Hạo'),
(3, 'Tao Nhã Và Quyết Đoán', 20, '2023-10-14', '../../img/img_sach/h3.jpg\r\n', 'Tao nhã bên ngoài, quyết đoán bên trong; tao nhã là vũ điệu, quyết đoán là sức mạnh; tao nhã là thời thượng, quyết đoán là văn hóa; tao nhã là Thần của một người, quyết đoán là linh hồn của người đó', '102.000', 'Vu Đình'),
(4, 'Khám Phá Bí Mật Chatgpt Và Ai Đỉnh Cao', 10, '2023-10-15', '../../img/img_sach/h4.jpg\r\n\r\n', 'ChatGPT, một công cụ trí tuệ nhân tạo mà chỉ vỏn vẹn trong 2 tháng kể từ khi ra mắt, đã thành hiện tượng toàn cầu. Với khả năng xử lý ngôn ngữ tự nhiên và sáng tạo nội dung vượt bậc, ChatGPT có thể giải đáp lưu loát tất cả các thắc mắc dù là ở bất kỳ lĩnh vực nào chỉ trong tích tắc. Sự bùng nổ của ChatGPT đã tạo ra vô vàn cơ hội mới để phát triển và nâng cao năng lực cạnh tranh cho các cá nhân và doanh nghiệp. Cuốn sách “Khám phá bí mật ChatGPT và AI đỉnh cao” của tác giả Nguyễn Minh Toàn sẽ cung cấp những kiến thức đỉnh cao và ứng dụng thực tế về AI và ChatGPT. Bạn sẽ được hướng dẫn chi tiết cách sử dụng AI và ChatGPT trong cuộc sống và kinh doanh, giúp bạn tiết kiệm thời gian và chi phí, đồng thời nâng cao hiệu suất công việc.', '243.000', '???'),
(5, 'Nghệ Thuật Hiện Diện', 20, '2023-10-15', '../../img/img_sach/h5.jpg', NULL, '95.000', ' Jacqueline Whitmore');

